    private void BuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BuscarActionPerformed
        if (usuarioT2.getText().isEmpty()) {

            JOptionPane.showMessageDialog(null, "hay un campo vacio");
        } else{
            if (UserLogic.encontrar(usuarioT2.getText())) {
           JOptionPane.showMessageDialog(null, "se envio su verificacion a su correo");
            this.dispose();

        } else {
            JOptionPane.showMessageDialog(null, "El usuario no fue encontrado");
        }
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BuscarActionPerformed

